    numSamples: 0,
    beginTransition: true
}

function deliver(msg)
{
    let store = context.get(msg.topic) || store0 ;
    if ((msg.writeThrough == false) && (store.payload == msg.payload))
        return null ;
                    // red : digest, yellow : update, green : writeThrough    

    let colour = msg.writeThrough ? 'green' : 'yellow' ;    
    node.status({ fill: colour, shape: "dot", text: `${msg.topic} ${store.payload}=>${msg.payload}`});
    store.payload = msg.payload ;
    context.set(msg.topic, msg);  
    return msg ;  
}
// ver 1.0 checked 22-09-2024

function digest(msg) {

    if (msg.reportOnDiff === undefined)
        msg.reportOnDiff = 1 ; 
    if (msg.windowSize === undefined)
        msg.windowSize = 10 ;   // arbitrary number

    let store = context.get(msg.topic);
    if (store === undefined)
        store = store0;

    const beginTransition = store.beginTransition;
    store.beginTransition = false;

    const valueDiff = store.numSamples ? Math.abs(msg.payload - store.lastAvg) : 0.0;
    const percentageDiff = store.numSamples ? (msg.payload - store.lastAvg) * 100 * 2 / (msg.payload + store.lastAvg) : 0.0;

    const endTransition = msg.writeThrough
                || (valueDiff >= msg.reportOnDiff) 
                || (store.numSamples + 1 >= msg.windowSize);  // write every maxGap  
    const outputAvg = beginTransition || endTransition;

    store.payload = msg.lastPayload = msg.payload;
    store.avg = (store.avg * store.numSamples + msg.payload) / (store.numSamples + 1);   // correct? 
    store.avg = toFix(store.avg, 2);
    store.numSamples += 1;
    msg.numSamples = store.numSamples;

    if (endTransition) 
    {
        node.status({ fill: "red", shape: "dot", text: `${msg.topic} ${msg.payload}  avg:${store.avg} num:${store.numSamples} `});
    }
    /*
    node.log(`${msg.topic} ${msg.lastPayload} avg: ${store.avg.toFixed(2)}, `
        + `diff% ${percentageDiff}% diff:${valueDiff} numSamples:${msg.numSamples} `
        + `bt ${beginTransition} et ${ endTransition }`);
    */
    if (outputAvg) {
        msg.payload = store.avg;
        store.numSamples = 0;
        if (endTransition)
        {
            store.lastAvg = store.avg;
            store.beginTransition = true;
        }
    }

    context.set(msg.topic, store);
    return outputAvg ? msg : null;
}

return msg.digest ?  digest(msg) : deliver(msg);

