function calculatePower(writeThrough)
{ 
    node.status({ fill: "red", shape: "dot", text: `${msg.topic}` });

    const instrumentation = global.get("instrumentation") || {}
    instrumentation[msg.topic] = msg.payload;

    const waterFlow = instrumentation["ashp0/PUMPSTAT_WAT_FLOW"] ;
    const ewt = instrumentation["ashp0/TEMP_EWT"];
    const lwt = instrumentation["ashp0/TEMP_LWT"];

    if (waterFlow !== undefined && ewt !== undefined && lwt !== undefined)
    {
        let power = (lwt - ewt) * waterFlow * 4.2 ;
        power = (Math.trunc(power * 100))/100 ; 

        instrumentation['ashp0/PUMPSTAT_CALC_POWER'] = power ;

        global.set("instrumentation", instrumentation);

        msg = { 
                prefix: 'local',
                topic: 'ashp0/PUMPSTAT_CALC_POWER', 
                source : 'ashp0', 
                location: 'PUMPSTAT_CALC_POWER', 
                measurement: 'pump',                
                payload: power, 
                writeThrough : writeThrough,
        }

        node.status({ fill: "green", shape: "dot", text: `power: ${power}kW` });

        return msg;
    }
    return null ;
} 



