function integrate(instrumentation, key, value) {
  const timeNow = Date.now();
  const entry = instrumentation[key] || { vt: 0, s: timeNow, e: 0, n: -1 }
  // first value ignored, just using timestamp
  const elapsed = timeNow - entry.s
  const vt = entry.vt + value * (elapsed - entry.e)
  const vth = vt / (60 * 60 * 1000)

