* @param {{ energy: any; }} instrumentation
*/

function collectData(msg, instrumentation) {
  const tokens = msg.topic.split("/")
  const sensorName = tokens[0]
  const sensorProperty = tokens[1]

  let m = null

  if (['temperature', "PUMPSTAT_CAPPOWER", "zeroCounters"].includes(sensorProperty)) {

    let avg = integrate(instrumentation, sensorName, Number(msg.payload))
    switch (sensorProperty) {
      case "PUMPSTAT_CAPPOWER":
        //node.error('hhh II ' + msg.topic + " " + sensorProperty + " " + instrumentation['ashp0/today/kWh'])

        let elapsedHours = instrumentation[sensorName].e / (1000 * 3600)
        let kWh = avg * elapsedHours
        instrumentation['ashp0/today/kWh'] = kWh // save to move to yesterday
        m = {
          topic: `ashp0/today/kWh`,  // update on each kWh consumed
          payload: Math.floor(kWh),
          digest: false
        }                // send update for each kWh
        break

      case "zeroCounters":

        node.status({ fill: "yellow", shape: "dot", text: `midnight : counter reset` });

        instrumentation['ashp0/yesterday/kWh'] = instrumentation['ashp0/today/kWh']
        instrumentation['ashp0/today/kWh'] = 0
        intergrateZero(instrumentation, 'ashp0/PUMPSTAT_CAPPOWER')

        m = {
          topic: `ashp0/yesterday/kWh`,
          payload: Math.floor(instrumentation['ashp0/yesterday/kWh']),
          digest: false
        }
        break

      case "temperature":
        if (sensorName in sensorWeight) {
          instrumentation[sensorName] = Number(msg.payload)
          const bt = calculateBuildingTemperature()
          m = {
            topic: `building/temperature`,
            payload: Math.round(10 * bt) / 10,   // update on 0.1 increments 
            digest: false
          }
        }
        break
    }
  }
  return m
}

