function zeroIntegral(key) 
{
  //const entry = 'key' in history ? history[key] : { vt: 0, s: timeNow, e: 0, n: -1 }
  const entry = history[key] ?? { vt: 0, s: timeNow, e: 0, n: -1 }

  entry.s += entry.e    // move the start time to end of last period
  entry.vt = 0          // zero the integral
  entry.e  = 0
  entry.n = 0           // need to be careful about initialising this
  history[key] = entry
}

function integral(key, value) 
{
  //const entry = 'key' in history ? history[key] : { vt: 0, s: timeNow, e: 0, n: -1 }
  const entry = history[key] ?? { vt: 0, s: timeNow, e: 0, n: -1 }
  // first value ignored, just using timestamp
  const elapsed = timeNow - entry.s

  entry.vt += value * (elapsed - entry.e)  // integral calculation
  entry.v = value
  entry.e = elapsed
  entry.n += 1
  history[key] = entry 

  const vth = entry.vt / (60 * 60 * 1000)

  //const en = { v: value, vt: vt, s: entry.s, e: elapsed, n: entry.n + 1 }

  const avg = elapsed ? entry.vt / elapsed : 0  // from the start

  const elapsedSecs = Math.floor(elapsed / 1000)
  if (true)
    node.status({
      fill: "red", shape: "dot",
      text: `${key} s:${getHHMM(entry.s)} e:${elapsedSecs} :${entry.vt} avg:${avg.toFixed(2)} h:${vth.toFixed(2)}`
    });
  return avg
}

