function ampsToKW(amps)
{
    return(Math.round(amps * 240) / 1000);    // watts to kW 
}

function calculatePower(ampsNow) {
    let e = context.get("energy");
    if (e === undefined)
        e = e0;

    //let kWh = null;

    e.sumOfAmpsSamples += ampsNow;
    e.numOfAmpsSamples += 1;
    let windowSize = samplesPerMinute * kWhPeriod;
    if (e.numOfAmpsSamples >= windowSize) {
        e.kWh = ampsToKW(e.sumOfAmpsSamples / e.numOfAmpsSamples);
        e.numOfAmpsSamples = 0;
        e.sumOfAmpsSamples = 0.0;
    }

    let beginTransition = e.begin;
    e.begin = false;

    let ampsDiff = e.numSamples ? ampsNow - e.amps : 0.0;
    let percentageDiff = e.numSamples ? (ampsNow - e.amps) * 100 * 2 / (e.amps + ampsNow) : 0.0;

    let endTransition = (Math.abs(ampsDiff) > 4 || e.numSamples >= 15 * maxGapMinutes);  // write every maxGap  
    let writeSample = beginTransition || endTransition;

    //let writeSample = percentageDiff > 60;
    let numSamples1 = e.numSamples;
    let kW = null;

    let ampsAvg = ((e.amps * e.numSamples) + ampsNow) / (e.numSamples + 1);   // correct? 
    e.amps = ampsAvg;    // store the new average
    e.numSamples += 1;

    if (writeSample) {
        kW = ampsToKW(ampsAvg);
        e.numSamples = 0;
        e.amps = 0.0;

        if (endTransition)
            e.begin = true;
    }
    if (false)
        node.warn("now " + ampsNow + " avg= " + ampsAvg.toFixed(2) + ", "
            + percentageDiff.toFixed(0) + "%,  amps diff= " + ampsDiff.toFixed(2)
            + " numSamples " + numSamples1 + " kWh " + e.kWh + " sum " + e.sumOfAmpsSamples);

    context.set("energy", e) ;

    // push out the last calculation of kWh 
    return kW ? { 'kW': kW, 'kWh': e.kWh, 'numSamples': numSamples1 } : null;
}


// calculate simple moving average
// https://en.wikipedia.org/wiki/Moving_average
// it's got a horrible shift in it.

function calculateSMA(lastPoint, dataPoints, windowSize) {
    let dataEmpty = {
        points: [],
        sum: 0.0,
        sma: 0.0
    }
    const sma = 0;
    let data = context.get(dataPoints);
    if (data === undefined)
        data = dataEmpty;

    // add last point and remove first point
    let numPoints = data.points.push(lastPoint); // returns new length

    //let lastSMA = data.sma;
    if (numPoints <= windowSize) {
        data.sum += lastPoint;
        data.sma = data.sum / numPoints;
    }
    else {
        const firstPoint = data.points.shift();
        numPoints -= 1;
        data.sma += ((lastPoint - firstPoint) / (numPoints))
    }
    context.set(dataPoints, data);
    return data.sma ;
}


